select
id, category_id, name, subtitle, main_image, price, stock, status, is_new, is_hot, is_banner, create_time,
update_time, sub_images, detail
from neuedu_product


    and id=#{productId}


    and name like #{productName}

select id, category_id, name, subtitle, main_image, price, stock, status, is_new, is_hot, is_banner, create_time, update_time, sub_images, detail from neuedu_product
and category_id IN
#{categoryId}
and name like #{keyword}

